mapper.select:
SELECT t.description, t.amount, t.tracker_date, t.type_id, cat.name as category_name, asst.name as asset_name FROM TRACKER t JOIN CATEGORY cat ON t.category_id = cat.id JOIN ASSET asst ON t.asset_id = asst.id WHERE t.user_id = #{userId}
AND t.type_id = #{typeId}
ORDER BY t.id DESC

SELECT name
FROM CATEGORY
WHERE user_id IS NULL OR user_id = #{userId}

    AND t.type_id = #{typeId}

ORDER BY name

SELECT name
FROM ASSET
WHERE user_id IS NULL OR user_id = #{userId}

    AND t.type_id = #{typeId}

ORDER BY name

SELECT COUNT(*)
FROM TRACKER
WHERE user_id = #{id}
